// Extract raw summaries
const summaries = items.map(item => item.json.output);

// Optional: cleanup function to remove any lingering headers or redundancy
function normalizeEmailSummary(summary) {
  return summary
    .trim()
    .replace(/^ðŸ“¨.*emails.*?:/i, '') // Remove repeated AI headers (just in case)
    .replace(/\s+/g, ' ') // Normalize whitespace
    .trim();
}

// Deduplicate & normalize
const processedSummaries = summaries
  .map(normalizeEmailSummary)
  .filter((summary, index, self) => self.indexOf(summary) === index && summary.length > 0)
  .filter((summary, index, self) => {
    const isSubset = self.some((other, i) => i !== index && other.includes(summary));
    return !isSubset;
  });

// Add numbering
const numberedList = processedSummaries.map((summary, index) => `${index + 1}. ${summary}`);

// Final output
const mergedText = [
  "ðŸ“¨ *These are your summarized emails from the past 24 hours:*",
  "",
  ...numberedList.map(line => `${line}\n`)
].join("\n");

return [
  {
    json: {
      message: mergedText.trim()
    }
  }
];

// Dynamically split email vs. rule items
const emailItems = items.filter(item => item.json.From); // Check for capital "From"
const ruleItems = items.filter(item => item.json.field && item.json.match_type && item.json.value);

// Case-insensitive match logic
function matchesRule(email, rule) {
  const field = rule.field?.toLowerCase();
  const matchType = rule.match_type?.toLowerCase();
  const value = rule.value?.toLowerCase();

  // Try both lowercase and capitalized field names (like 'from' vs 'From')
  let emailField = email[field] || email[field.charAt(0).toUpperCase() + field.slice(1)];
  if (!emailField) return false;

  let emailValue = emailField.toLowerCase();

  // Special handling for extracting email address from "From"
  if (field === 'from') {
    const match = emailValue.match(/<(.+?)>/);
    if (match) emailValue = match[1];
  }

  switch (matchType) {
    case 'contains':
      return emailValue.includes(value);
    case 'equals':
      return emailValue === value;
    case 'startswith':
      return emailValue.startsWith(value);
    case 'endswith':
      return emailValue.endsWith(value);
    default:
      return false;
  }
}

const matchingEmails = [];

for (const emailItem of emailItems) {
  const email = emailItem.json;
  let isBlocked = false;

  for (const rule of ruleItems.map(r => r.json)) {
    if (matchesRule(email, rule)) {
      isBlocked = true;
      break;
    }
  }

  if (!isBlocked) {
    matchingEmails.push({ json: email }); // Only keep if NOT blocked
  }
}

return matchingEmails;
